(1524, 273, 'produtos-single-caracteristicas', '<ul>\r\n 	<li>Alto poder de processamento (SoC Zynq®️ UltraScale+ EV)</li>\r\n 	<li>Módulo eSIM para comunicação celular</li>\r\n 	<li>Software embarcado como Linux aplicável com o Docker</li>\r\n 	<li>Ambiente Seguro Global Platform (Trusted Execution Environment)</li>\r\n 	<li>Reconhecimento Óptico de Caracteres (OCR) de 15 frames por segundo.</li>\r\n 	<li>Aceleração por hardware para OCR/LPR (License Plate Recognition) e Processamento de Imagem dos analíticos Pumatronix.</li>\r\n 	<li>Acelerador de Rede Neural (NPU) compatível com Tensorflow® and Caffe®</li>\r\n 	<li>Módulo de Assinatura Digital</li>\r\n 	<li>Sensor de imagem 2MP Global Shutter 1/1.8”</li>\r\n 	<li>Lente motorizada (LM) 13-55mm ou CS mount</li>\r\n 	<li>Auto íris (DC e P-IRIS)</li>\r\n 	<li>Sistema de disparo de flash sincronizado (linha ITSLUX)</li>\r\n 	<li>264 e H.265</li>\r\n 	<li>Alimentação PoE (Power over Ethernet IEEE 802.3af) opcional ou tensão entre 9~32V corrente DC</li>\r\n 	<li>GPS Integrado</li>\r\n</ul>'),
(1525, 273, '_produtos-single-caracteristicas', 'field_6068f0a5fad90'),
(1526, 273, 'produtos-single-beneficios', '<ul>\r\n 	<li>Plataforma aberta</li>\r\n 	<li>Captura e processa os vídeos na própria câmera</li>\r\n 	<li>Pronta para Inteligência Artificial e Deep Learning</li>\r\n 	<li>Dispensa a utilização de computadores e roteadores</li>\r\n 	<li>Estrutura metálica resistente, com ótima dissipação de calor</li>\r\n 	<li>Processamento para mais de 02 faixas</li>\r\n 	<li>Sincronismo com Flash por Hardware</li>\r\n 	<li>Baixo consumo de energia</li>\r\n 	<li>Permite assinatura digital</li>\r\n 	<li>ROI (Region of Interest), demarcações de área específicas para a detecção de movimento pelo algoritmo.</li>\r\n</ul>'),
