(1838, 289, 'produtos-single-caracteristicas', '<ul>\r\n 	<li>É compatível com sistema operacional Linux em plataformas x86, x86_64 e ARM® A53</li>\r\n 	<li>Linguagens Java e Python</li>\r\n 	<li>Imagens nos formatos jpg, bmp e png</li>\r\n 	<li>Imagens coloridas e em escala de cinza</li>\r\n 	<li>API em linguagem C</li>\r\n 	<li>Fácil integração com qualquer ambiente de programação.</li>\r\n</ul>'),
(1839, 289, '_produtos-single-caracteristicas', 'field_6068f0a5fad90'),
(1840, 289, 'produtos-single-beneficios', '<ul>\r\n 	<li>Permite a classificação de vários veículos em uma mesma imagem;</li>\r\n 	<li>Identificação em variadas categorias: motocicleta, carro, ônibus <span style=\"text-decoration: line-through;\">ou</span> e caminhão;</li>\r\n 	<li>Índices de acuracidade acima de 80%;</li>\r\n 	<li>Ideal para uso em imagens com resolução 640x480 ou superior;</li>\r\n 	<li>Pode ser usado em imagens frontais ou traseiras dos veículos;</li>\r\n 	<li>Classifica imagens noturnas, diurnas, fechadas ou panorâmicas.</li>\r\n</ul>'),
