(2032, 299, 'produtos-single-caracteristicas', '<strong>VTR1</strong>\r\n<ul>\r\n 	<li>Possui GPS integrado;</li>\r\n 	<li>Acompanha modem 3G/4G;</li>\r\n 	<li>Integração via protocolo REST (WebService);</li>\r\n 	<li>Possui roteador Wi-Fi integrado.</li>\r\n 	<li>Possui suporte para uma (01) câmera</li>\r\n 	<li>O sistema vem acomodado em uma Unidade de Processamento em forma de maleta compacta.</li>\r\n 	<li>Cooler de Ventilação</li>\r\n</ul>\r\n<strong>VTR4</strong>\r\n<ul>\r\n 	<li>Suporta até 4 câmeras OCR e 2 panorâmicas</li>\r\n 	<li>O sistema vem acomodado em uma Unidade de Processamento em forma de maleta tamanho 38,5 x 30 cm</li>\r\n 	<li>Cooler de Ventilação</li>\r\n</ul>'),
(2033, 299, '_produtos-single-caracteristicas', 'field_6068f0a5fad90'),
(2034, 299, 'produtos-single-beneficios', '<strong>VTR1 </strong>\r\n<ul>\r\n 	<li>Atua como sistema portátil de apoio a operações remotas, tais como rondas e blitz;</li>\r\n 	<li>Patrulhamento ostensivo</li>\r\n 	<li>Realiza a leitura automática de placas em alta velocidade e fluxo de veículos;</li>\r\n 	<li>Geração de dados estatísticos de fluxo veicular</li>\r\n 	<li>Fácil manuseio e transporte na operação.</li>\r\n</ul>\r\n<strong>VTR4</strong>\r\n<ul>\r\n 	<li>Oferece um campo de visão mais amplo.</li>\r\n 	<li>Pode ser acoplado em ônibus para monitoramento de estacionamento em faixas exclusivas.</li>\r\n 	<li>Realiza a leitura automática de placas em alta velocidade e fluxo de veículos;</li>\r\n 	<li>Geração de dados estatísticos de fluxo veicular</li>\r\n 	<li>Fácil manuseio e transporte na operação.</li>\r\n</ul>'),
